import React, { Component, input } from 'react';
import './App.css';
import Customers from './components/customers';
import { Button } from 'reactstrap';
import { Container, Row, Col } from 'react-grid-system';
import searchIcon from'./assets/search_icon.png';
import greyStar from'./assets/grey_star.png';
import greenStar from'./assets/green_star.png';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

library.add(faStar);

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      isFavorite: false,
      starColor: "#aaaaaa"
     }
  }

  starClicked = () => {
    if (this.state.starColor == "#aaaaaa") {
      this.setState({starColor: "#22975e"})
    }
    else if (this.state.starColor == "#22975e") {
      this.setState({starColor: "#aaaaaa"})
    }
    this.setState({isFavorite: !this.state.isFavorite})
    console.log(this.state.isFavorite)
  }



  render() {
    return (
      <div className="App">
          <Row className="mainHeader">
            <Col sm={12} className="textMainHeader">
              Toronto Waste Lookup
            </Col>
          </Row>
          <Row className="searchBoxParent">
            <Col sm={11}>
               <input type="text" name="name" className="searchBox"/>
            </Col>
            <Col sm={1}>
              <Button className="searchButton">
               <img src={searchIcon}/>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col sm={6}>
              <Row>
                <Col sm={1}>
                  <Button className="starButton" onClick={this.starClicked}>
                      <FontAwesomeIcon
                          icon="star"
                          color={this.state.starColor}
                        />
                  </Button>
                </Col>
                <Col sm={11}>
                  Blue Bin (plastic takeout food and produce containers)
                </Col>
              </Row>
            </Col>
            <Col sm={6}>
              One of three columns
            </Col>
          </Row>
          <Row className="favouritesHeader">
            <Col sm={2} className="textFavouritesHeader">
              Favourites
            </Col>
          </Row>
          <Row className="favouritesList">
            <Col sm={6}>
              One of three columns
            </Col>
            <Col sm={6}>
              One of three columns
            </Col>
          </Row>
        <Customers/>
      </div>
    );
  }
}

export default App;
