import React, { Component, input } from 'react';
import './App.css';
import Customers from './components/customers';
import { Button, Table } from 'reactstrap';
import { Container, Row, Col } from 'react-grid-system';
import searchIcon from'./assets/search_icon.png';
import greyStar from'./assets/grey_star.png';
import greenStar from'./assets/green_star.png';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

library.add(faStar);

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      isFavourite: false,
      starColor: "#aaaaaa",
      searchResults: [],
      favouritesList: []
     }
  }

  componentDidMount() {
    fetch('/api/searchResults')
      .then(res => res.json())
      .then(searchResults => this.setState({searchResults}, () => console.log('Search results..', searchResults)));
  }

  starClicked = (favouriteIncoming) => {
    const favourite = {
      id: favouriteIncoming.id,
      title: favouriteIncoming.title,
      instruction: favouriteIncoming.instruction,
      isFavourite: !favouriteIncoming.isFavourite
    };

    console.log(favouriteIncoming.isFavourite);
    if (favouriteIncoming.isFavourite === false) {
      this.setState({
        starColor: "#22975e",
        isFavourite: !this.state.isFavourite,
        favouritesList: [...this.state.favouritesList, favourite]
      })
    }
    else if (favouriteIncoming.isFavourite === true) {
      this.setState({
        starColor: "#aaaaaa",
        isFavourite: !this.state.isFavourite,
        favouritesList: [...this.state.favouritesList, favourite]})
    }
  };

  render() {
    return (
      <div className="App">
          <Row className="mainHeader">
            <Col sm={12} className="textMainHeader">
              Toronto Waste Lookup
            </Col>
          </Row>
          <Row className="searchBoxParent">
            <Col sm={11}>
               <input type="text" name="name" className="searchBox"/>
            </Col>
            <Col sm={1}>
              <Button className="searchButton">
               <img src={searchIcon}/>
              </Button>
            </Col>
          </Row>
          <Row>
            <Table borderless>
              <tbody>
                {this.state.searchResults.map(searchResult => 
                  <tr>
                    <td key={searchResult.id} className="searchResultsRow">
                      <Row>
                        <Col sm={5}>
                          <Row>
                            <Col sm={1}>
                              <Button className="starButton" onClick={() => this.starClicked(searchResult)}>
                                    {searchResult.isFavourite === false &&
                                      <FontAwesomeIcon
                                        className="startIcon"
                                        icon="star"
                                        color="#aaaaaa"
                                        size="2x"
                                      />
                                    }
                                    {searchResult.isFavourite === true &&
                                      <FontAwesomeIcon
                                        className="startIcon"
                                        icon="star"
                                        color="#22975e"
                                        size="2x"
                                      />
                                    }
                              </Button>
                            </Col>
                            <Col sm={11} className="searchResultTitle">
                              {searchResult.title}
                            </Col>
                          </Row>
                        </Col>
                        <Col sm={7}>
                          {searchResult.instruction}
                        </Col>
                      </Row> 
                    </td>
                  </tr> 
                )}
              </tbody>
            </Table>
          </Row>
          <Row className="favouritesHeader">
            <Col sm={2} className="textFavouritesHeader">
              Favourites
            </Col>
          </Row>
          <Row className="favouritesList">
            <Table borderless>
              <tbody>
                {this.state.favouritesList.map(favourite => 
                  <tr>
                    <td key={favourite.id} className="searchResultsRow">
                      <Row>
                        <Col sm={5}>
                          <Row>
                            <Col sm={1}>
                              <Button className="starButton" onClick={(favourite) => this.starClicked(favourite)}>
                                  <FontAwesomeIcon
                                      className="startIcon"
                                      icon="star"
                                      color={this.state.starColor}
                                      size="2x"
                                    />
                              </Button>
                            </Col>
                            <Col sm={11} className="searchResultTitle">
                              {favourite.title}
                            </Col>
                          </Row>
                        </Col>
                        <Col sm={7}>
                          {favourite.instruction}
                        </Col>
                      </Row> 
                    </td>
                  </tr> 
                )}
              </tbody>
            </Table>
          </Row>
        {/* <Customers/> */}
      </div>
    );
  }
}

export default App;
